import React, { useEffect, useState } from 'react'
import axios from 'axios'


function Users() {

    const [users, setUsers] = useState([])
    const [search,setSearch] = useState('')

    const searchChange = (e) =>{
        setSearch(e.target.value)
    }
    const getUsers = async () => {

        try {
           let response = await axios.get("https://api.github.com/users")
           setUsers(response.data)
           
        } catch (error) {
            console.error(error)
        }
    }

    useEffect(() => {
        getUsers()
    }, [])
    const resultSearch = async () => {

        try {
           let response = await axios.get("https://api.github.com/users?q=")
           setSearch(response.data)
           
        } catch (error) {
            console.error(error)
        }
    }

    useEffect(() => {
        getUsers()
    }, [])

    return (
        <div>
           <div className="row">
               <div className="col-md6">
                   <hr/>
                   <h1>List Of Users</h1>
                   </div>
               <div className="col-md6 text-end">
               <input onChange={searchChange} type="text" className="form-control" placeholder="Search a user" />
                <button onClick={resultSearch} className="btn btn-outline-secondary" type="search" >Search</button>
               </div>
           </div>

            <hr />

            <div className="row">
                {users && users.map(user => (
                    <div className="col-md-3" key={user.id}>
                        <div className="card my-3">
                            <img className="card-img-top" src={user.avatar_url} alt={user.login} />
                            <div className="card-body">
                                <h4 className="card-title">{user.login}</h4>
                                <div className="card-text">
                                    <a href={user.html_url} className='btn btn-danger'>Show more</a>
                                </div>
                            </div>
                        </div>
                    </div>

                ))}
            </div>
        </div>
    )
}

export default Users
