import './App.css';
import { useState } from 'react';
import Navbar from './components/Navbar/navbar';
import "/node_modules/bootswatch/dist/solar/bootstrap.min.css"
function App() { 
  const [title, setTitle] = useState("Reda")
  const [courses,setCourses] = useState([
  {id:1,title:"Learn NoeJS"},
  {id:2,title:"Learn ReactJS"},
  {id:3,title:"Learn Angular"},
  {id:4,title:"Learn Laravel"},


  ])
  const handleClick = () => {
    setTitle("Mondiapolis University")
  }
  const deleteOneCourse = (id) => {
    setCourses(courses.filter(course => id !== course.id))
  }
  return (

    <div className="App">
       <Navbar />
{title}
<button onClick={handleClick}>Click me</button>
<hr />
{courses.map((course,index )=> (
  <div key={index}>
    <h2> {index} {course.id} {course.title} </h2>
    <button onClick={ () => deleteOneCourse(course.id)}>Delete</button><div/>
    </div>
    ) )}
    </div>
  );
}

export default App;
